  // 1. Get the long string from the 'Promoted Products' field.
  const promotedProductsString = item.json['Promoted Products'];

  // 2. Check if the field actually exists and is a non-empty string.
  //    If not, we'll just skip to the next item.
  if (promotedProductsString && typeof promotedProductsString === 'string') {

    // 3. Split the string by comma, semicolon, OR colon using a Regular Expression.
    //    Then, map over the results to trim whitespace from each code.
    //    Finally, filter out any empty entries that might result from double delimiters (e.g., "A,,B").
    const skyCodes = promotedProductsString
      .split(/[;,:]/)
      .map(code => code.trim())
      .filter(code => code.length > 0);

    // 4. Loop through the clean array of codes and create the new fields.
    skyCodes.forEach((code, index) => {
      // Create a dynamic key like "product1", "product2", etc.
      // (index is 0-based, so we add 1)
      item.json[`product${index + 1}`] = code;
    });
  }
}

// Return all the modified items.
return $input.all();

