{{ ($('17. Klaviyo: Create Campaign (Draft)').item?.json.attributes.email_config || {}).subject || "" }}
{{ ($('17. Klaviyo: Create Campaign (Draft)').item?.json.attributes.email_config || {}).html_content || "" }}

